import React, { useState, useContext } from 'react';
import './App.css';
import { Label, Note } from "./types";
import { dummyNotesList } from './constants';
import heartEmpty from './images/heart_empty.png';
import heartFull from './images/heart_full.png';
import { ThemeContext, themes } from "./ThemeContext";

const initList: Note[] = [];

function App() {
    const theme = useContext(ThemeContext); // Access theme from context
    const [currentTheme, setCurrentTheme] = useState(theme); // Manage theme locally

    const toggleTheme = () => {
        setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

    const [notes, setNotes] = useState(dummyNotesList); 
    const initialNote = {
        id: -1,
        title: "",
        content: "",
        label: Label.other,
        favorite: false,
    };
    const [createNote, setCreateNote] = useState(initialNote);

    const createNoteHandler = (event: React.FormEvent) => {
        event.preventDefault();
        setCreateNote({ ...createNote, id: notes.length + 1 });
        setNotes([...notes, createNote]);
    };

    const handleDelete = (note: Note) => {
        setNotes(notes.filter((n) => n !== note));
    };

    const [favoriteList, setFavoriteList] = useState(initList);
    const toggleFavorite = (note: Note) => {
        note.favorite = !note.favorite;
        setFavoriteList(favoriteList.includes(note) ? favoriteList.filter((n) => n !== note) : [...favoriteList, note]);
    };

    return (
        <div className='app-container' style={{ background: currentTheme.background, color: currentTheme.foreground }}>
            <form className="note-form" onSubmit={createNoteHandler}>
                <div><input placeholder="Note Title" onChange={(e) => setCreateNote({ ...createNote, title: e.target.value })} required /></div>
                <div><textarea onChange={(e) => setCreateNote({ ...createNote, content: e.target.value })} required /></div>
                <div>
                    <select onChange={(e) => setCreateNote({ ...createNote, label: e.target.value as Label })} required>
                        <option value={Label.personal}>Personal</option>
                        <option value={Label.study}>Study</option>
                        <option value={Label.work}>Work</option>
                        <option value={Label.other}>Other</option>
                    </select>
                </div>
                <div><button type="submit">Create Note</button></div>
                <button onClick={toggleTheme}>Toggle Theme</button> {/* Toggle theme button */}
            </form>

            <div className="notes-grid">
                {notes.map((note) => (
                    <div key={note.id} className="note-item">
                        <div className="notes-header">
                            <img onClick={() => toggleFavorite(note)} style={{ width: 20 }} src={note.favorite ? heartFull : heartEmpty} alt="favorite" />
                            <button onClick={() => handleDelete(note)}>x</button>
                        </div>
                        <h2 contentEditable="true">{note.title}</h2>
                        <p contentEditable="true">{note.content}</p>
                    </div>
                ))}
            </div>

            <div className="favorited-notes">
                {favoriteList.map((note) => <li key={note.id}>{note.title}</li>)}
            </div>
        </div>
    );
}

export default App;
